<template>
    <a-row>
        <a-col :span="14">
            <a-carousel arrows dots-class="slick-dots slick-thumb">
                <template #customPaging="props">
                    <a>
                        <img alt="img" :src="getImgUrl(props.i)" />
                    </a>
                </template>
                <div v-for="image in product.images" :key="image.id">
                    <img alt="img" :src="image.path" />
                </div>
            </a-carousel>
        </a-col>
        <a-col :span="10">
            <a-typography-title :level="2">
                <a-space>
                    {{ product.name }}
                    <AddProductToCart :product="product" />
                </a-space>
            </a-typography-title>
            <a-typography-title :level="3">
                $ {{ product.sale_price.value }} -
                {{
                    this.$page.props.default_currency.alphabetic_code
                }}</a-typography-title
            >
            <a-divider />
            <a-descriptions>
                <a-descriptions-item label="Quantity">{{
                    product.quantity
                }}</a-descriptions-item>
                <a-descriptions-item label="Weight"
                    >{{ product.weight.value }}
                    {{ product.weight.unit }}</a-descriptions-item
                >
            </a-descriptions>
            <a-typography-paragraph>
                {{ product.description }}
            </a-typography-paragraph>
        </a-col>
    </a-row>
</template>

<script>
import { defineComponent } from "vue";
import { ShoppingOutlined, ShoppingCartOutlined } from "@ant-design/icons-vue";
import AddProductToCart from "@/Pages/Product/Partials/AddProductToCart.vue";

export default defineComponent({
    props: {
        product: Object,
    },
    components: {
        ShoppingOutlined,
        ShoppingCartOutlined,
        AddProductToCart,
    },
    methods: {
        getImgUrl(i) {
            return this.product.images[i].path;
        },
    },
    setup(props) {
        if (props.product.images.length == 0) {
            props.product.images.push({
                path: "data:image/png;base64,",
            });
        }
    },
});
</script>

